
public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//when condition of expression is false then body of loop is not executed
		//it is required to execute body of loop atleast once even if condition is false
		/*
		do
		{
			<<>statement>>
		}while condition
		*/
		
		int num=3;
		do
		{
			System.out.println("welcome to dowhile" );
			num--;
		}while (num>3);
	}

}
